select sum(group_score)/count(group_score) as track_score,sum(group_prod)/count(group_score) as track_prod,sum(group_quality)/count(group_score) as track_quality,sum(group_behavior)/count(group_behavior) as track_behavior,sum(group_compliance)/count(group_compliance) as track_compliance  from (select (select top 1 group_score from group_scores where leader_sap = sapno order by date desc) as group_score,(select top 1 group_prod from group_scores where leader_sap = sapno order by date desc) as group_prod,(select top 1 group_quality from group_scores where leader_sap = sapno order by date desc) as group_quality,(select top 1 group_behavior from group_scores where leader_sap = sapno order by date desc) as group_behavior,(select top 1 group_compliance from group_scores where leader_sap = sapno order by date desc) as group_compliance from (select distinct(leader_sap) as sapno from group_scores where track LIKE '%Sleep EQ%') tb) tbl